import { DatePipe, DecimalPipe } from "@angular/common";

export class Employee {
  id?: string;
  department?: string;
  fullName?: string;
  dateOfBirth?: string;
  dateOfEmployment?: string;
  salary?: DecimalPipe;
  constructor(
    id?: string,
    department?: string,
    fullName?: string,
    dateOfBirth?: Date,
    dateOfEmployment?: Date,
    salary?: DecimalPipe,
  )
  {
    this.id = id;
    this.department = department;
    this.fullName = fullName;
    this.dateOfBirth = dateOfBirth?.getDay().toString() + "." + dateOfBirth?.getMonth().toString() + "." + dateOfBirth?.getFullYear().toString();
    this.dateOfEmployment = dateOfEmployment?.getDay().toString() + "." + dateOfEmployment?.getMonth().toString() + "." + dateOfEmployment?.getFullYear().toString();
    this.salary = salary;
  }
}
