import { Component, OnInit, ViewChild } from '@angular/core';
import { EmployeeService } from './employee.service';
import { Employee } from './Employee';
import { Observable, Subject } from 'rxjs'; 
import { ColDef, GridOptions, SelectionChangedEvent } from 'ag-grid';
import { AgGridAngular } from 'ag-grid-angular';

@Component({
  selector: 'app-employees',
  templateUrl: './employees.component.html',
  providers: [EmployeeService]
})
export class EmployeesTestComponent implements OnInit{

  employee: Employee = new Employee();
  columnDefs: any[] = [
    { headerName: 'Id', field: "id", sortable: "true", filter: "true", hide: "true" },
    { headerName: 'Отдел', field: "department", sortable: "true", filter: "true" },
    { headerName: 'Полное имя', field: "fullName", sortable: "true", filter: "true" },
    {
      headerName: 'Дата рождения', field: "dateOfBirth", sortable: "true", filter: "agDateColumnFilter", cellRenderer: (data: { value: string | number | Date; }) => {
        return data.value ? (new Date(data.value)).toLocaleDateString() : '';
      } },
    {
      headerName: 'Дата приема на работу', field: "dateOfEmployment", sortable: "true", filter: "agDateColumnFilter", cellRenderer: (data: { value: string | number | Date; }) => {
        return data.value ? (new Date(data.value)).toLocaleDateString() : '';
      } },
    { headerName: 'Зарплата', field: "salary", sortable: "true", filter: "true" }
  ];
  rowData: Employee[] = [];

  @ViewChild(AgGridAngular) agGrid!: AgGridAngular; 

  constructor(private employeeService: EmployeeService) { }

  ngOnInit(): void {
    this.loadEmployees();
  }

  loadEmployees() {
    this.employeeService.getEmployees()
      .subscribe(data => {
        this.rowData = data;
      });
  }

  save() {
    if (this.employee.id == null) {
      this.employeeService.createEmployee(this.employee)
        .subscribe((data: Employee) => this.rowData.push(data));
      this.agGrid.api.setRowData(this.rowData);
    } else {
      this.employeeService.updateEmployee(this.employee)

    }
    this.clearSelection();
  }

  delete(p: Employee) {
    this.employeeService.deleteEmployee(p.id)
      .subscribe(() => this.employeeService.getEmployees()
        .subscribe(data => { this.rowData = data}));

    this.clearSelection();
  }

  //editEmployee(p: Employee) {
  //  this.employee = p;
    
  //}

  add() {
    this.clearSelection();
  }

  //cancel() {
  //  this.employee = new Employee();
  //}

  onSelection() {
    //if (this.agGrid.api.getSelectedRows().length == 1)
    //   this.employee = this.agGrid.api.getSelectedRows()[0];
  }

  clearSelection() {
    this.agGrid.api.deselectAll();
    this.employee = new Employee();
  }
}
<h1 id="tableLabel">Список сотрудников</h1>

<p *ngIf="!rowData"><em>Loading...</em></p>

<div class="d-flex flex-row">

  <div>
    <ag-grid-angular style="width: 1000px; height: 600px;"
                     class="ag-theme-alpine"
                     [columnDefs]="columnDefs"
                     [rowSelection]="'single'"
                     [rowData]="rowData"
                     (selectionChanged)="onSelection()">
    </ag-grid-angular>
  </div>

    <div class="btn-group-vertical" role="group">
      <button class="btn btn-primary" (click)="add()" data-bs-toggle="modal" data-bs-target="#createModal">Добавить</button>
      <button class="btn btn-primary" [disabled]="employee.id == null" data-bs-toggle="modal" data-bs-target="#createModal">Редактировать</button>
      <button class="btn btn-primary" [disabled]="employee.id == null" data-bs-toggle="modal" data-bs-target="#deleteModal">Удалить</button>
    </div>
</div>

<button class="btn btn-primary" (click) ="clearSelection()">Сбросить выделение</button>
<!--Модальное окно добавления-->

<div class="modal fade" id="createModal" data-bs-backdrop="static" tabindex="-1" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h4 class="modal-title">Добавление сотрудника</h4>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Закрыть"></button>
      </div>


      <div class="modal-body">

        <div>
